//======================================
//add color
//======================================
/*

	// add font api params

    engine.sketch.fonts.add(font_tag_string,font_name_string,location_url_string,is_global_boolean);

    1 font_tag_string can be used to get the font name by font get api.

    2 font_name_string will be the tag the font will be available in css and js render apis ie if the font name given is "sampleFont" you can use "sampleFont" in css to use this font.

    3 location_url_string 

    	- if font is hosted locally only relative path is required ie if the vegana app is hosted with some.com domain and font ia available at 	some.com/css/font_1.ttf then the relative font path should be css/font_1.ttf

    4 is_global_boolean true if you use full url but if local defaults to false.

*/

const load_font = await engine.sketch.fonts.add("sampleFont","sample-font","assets/fonts/Montserrat-Regular.ttf")
.then(()=>{
	return true;
})
.catch(()=>{
	return false;
});

console.log({load_font:load_font});

//======================================
//get color
//======================================

let sampleFont = engine.sketch.fonts.get("sampleFont");

console.log({sampleFont:sampleFont});

//======================================
//use font with get api
//======================================

engine.make.div({
	parent:pageId,
    text:'see my font',
    draw:{
    	all:{
        	border:"5px solid purple",
            padding:"10px",
            //======================================
            //used font with get api
            'font-family':sampleFont
            //======================================
        }
    }
});

//======================================
//use font by name
//======================================

engine.make.div({
	parent:pageId,
    text:'i used font name',
    draw:{
    	all:{
        	border:"5px solid purple",
            padding:"10px",
            //======================================
            //used font name
            'font-family':"sample-font"
            //======================================
        }
    }
});
